REPLACE INTO `npc_text_locale` VALUES (988881, 'deDE', 'Grüße $n. \r\nDu kannst Deine Rassenvorteile und Fähigkeiten mit denen des Blutelfen austauschen. Wenn die Rasse, die Sie ausgewählt haben, kein $c hat, wirst Du deine $c Rassenvorteile verlernen. \r\nSie lernen Arkane Affinität, Magieresistenz und wenn Sie ein Todesritter, Jäger, Magier, Paladin, Schurke sind, oder Hexenmeister, du wirst Arcane Torrent lernen. \r\n\r\nWas ist dein Wunsch?', '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
